
const cas = require("../../cas.js");
const assert = require("assert");
const path = require("path");

(async () => {
    const browser = await cas.newBrowser(cas.browserOptions());
    let success = false;
    try {
        const page = await cas.newPage(browser);
        await cas.gotoLogin(page);
        await cas.loginWith(page);
        const page2 = await cas.newPage(browser);
        await page2.setRequestInterception(true);
        await page2.on("request", (request) => {
            const data = {
                "method": "POST",
                "postData": "%2BPHNhbWwycDpTZXNzaW9uSW5kZXg%2BZmFrZXNlc3Npb25pbmRleDwvc2FtbDJwOlNlc3Npb25JbmRleD48L3NhbWwycDpMb2dvdXRSZXF1ZXN0Pg%3D%3D",
                "headers": {
                    ...request.headers(),
                    "Content-Type": "application/x-www-form-urlencoded"
                }
            };
            request.continue(data);
        });
        await cas.goto(page2, "https://localhost:8443/cas/idp/profile/SAML2/POST/SLO");
        await cas.logPage(page2);
        assert(await page2.url().startsWith("https://localhost:9859/get"));
        success = true;
    } finally {
        if (success) {
            await cas.removeDirectoryOrFile(path.join(__dirname, "/saml-md"));
        }
        await browser.close();
    }
})();
